import React from 'react';
import Button from './Button';
import { Paragraph } from './Fonts';

class ShowEmail extends React.Component {
  state = { show: false }

  renderContactInfo = () => {
    let name = 'info'
    let at = '@'
    let domain = 'zeltta'
    let dot = '.'
    let com = 'COM'
    return `${name}${at}${domain}${dot}.${com.toLowerCase()}`
  }

  render() {
    const { show } = this.state;
    console.log(show)
    if (show) {
      return (
        <Paragraph>{this.renderContactInfo}</Paragraph>
      )
    }
    return (
      <Button label="Show email" onClick={() => this.setState((prevState) => { show: prevState.show ? false : true })}/>
    )
  }
}

export default ShowEmail;
