import React from 'react';
import styled from 'emotion/styled';
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Logo from '../atoms/Logo';

const App = (props) => {
  return (
    <Container style={{ backgroundColor: '#000', position: 'fixed', top: '0px', left: '0px', right: '0px', bottom: '0px', overflowY: 'scroll' }}>
      {props.children}
    </Container>
  )
}

const Layout = (props) => {
  const { style, children } = props;
  const InnerDiv = styled.div({
    ...style,
    width: '1000px',
    flex: 1,
    display: 'flex',
    flexDirection: 'row',
    '@media screen and (max-width: 1000px)': {
      width: '100vw',
      flexDirection: 'column',
      backgroundColor: '#000',
    },
  });
  return (
    <Row style={{
      flex: 1,
      flexDirection: 'row',
      display: 'flex',
      justifyContent: 'center',
      padding: '20px'
    }}>
      <InnerDiv>
        {children}
      </InnerDiv>
    </Row>
  )
}

const CutPathDiv = ({ top, backgroundColor, children, height }) => {
  return (
    <Row style={{ 
      height: height ? height : '112vh',
      position: 'relative',
      backgroundColor: backgroundColor ? backgroundColor : '#000',
      top : top ? top : '0vh',
      lexDirection: 'row',
      display: 'flex',
      justifyContent: 'center',
      clipPath: 'polygon(0 0, 100% 10%, 100% 100%, 0 100%)', 
      WebkitClipPath: 'polygon(0 0, 100% 10%, 100% 100%, 0 100%)'}}>
        {children}
    </Row>
  )
}

const HeroSection = (props) => {
  const { style, children } = props;
  return (
    <Row style={{
      backgroundColor: '#000', 
          backgroundImage: 'url(/static/hero-image.jpeg)',
          backgroundPosition: 'center',
          backgroundSize: 'cover',
          position: 'relative',
          flexDirection: 'column',
          display: 'flex',
          height: '100vh',
    }}>
    <Row style={{
          backgroundColor: 'rgba(10,0,80,0.5)',
          flex: 1,
          display: 'flex',
    }}>
      <Layout style={{
        display: 'flex',
        alignItems: 'center'
      }}>
        <h1 style={{ 
          fontFamily: 'Montserrat, sans-serif', 
          fontSize: 52,
          color: '#fff'
        }}> Developing <br/> technology <br/> products</h1>
        </Layout>
      </Row>
    </Row>
  )
}

const FooterSection = (props) => {
  const { style, children } = props;
  return (
    <Row style={{
      backgroundColor: '#000', 
      position: 'relative',
      bottom: '36vh',
      flexDirection: 'column',
      display: 'flex',
      height: '100vh',
    }}>
    <Row style={{
      backgroundColor: 'rgba(10,0,80,0.5)',
      flex: 1,
      display: 'flex',
    }}>
      <Layout style={{
        display: 'flex',
        alignItems: 'center'
      }}>
        <h1 style={{ 
          fontFamily: 'Montserrat, sans-serif', 
          fontSize: 52,
          color: '#fff'
        }}> Contact</h1>
        </Layout>
      </Row>
    </Row>
  )
}

const Index = () => {
  return (
    <App>
      <Logo />
        <HeroSection />
        <CutPathDiv top={'-12vh'} backgroundColor={'#4400ff'}>
          <Layout style={{
              alignItems: 'center',
            }}>
            <div style={{ flex: 1 }}>
              <h1 style={{ 
                fontFamily: 'Montserrat, sans-serif', 
                fontSize: 52,
                color: '#fff'
              }}>Design first</h1>
            </div>
            <div style={{ flex: 1 }}>
              <img src="/static/phone.png" alt="stack logo" style={{ height: '800px', width: '500px'  }} />
            </div>
          </Layout>
        </CutPathDiv>
        <CutPathDiv top={'-24vh'} backgroundColor={'#00ffaa'}>
          <Layout style={{
              display: 'flex',
              alignItems: 'center'
            }}>
            <Col style={{ flex: 1 }}>
              <h1 style={{ 
                fontFamily: 'Montserrat, sans-serif', 
                fontSize: 52,
                color: '#000'
              }}>Full Stack</h1>
            </Col>
            <Col style={{ flex: 1 }}>
              <img src="/static/stack.svg" alt="stack logo" style={{ height: '400px', width: '400px'  }} />
            </Col>
          </Layout>
        </CutPathDiv>
        <CutPathDiv top={'-36vh'} backgroundColor={'#fff'} height={'112vh'}>
          <Layout style={{
              display: 'flex',
              alignItems: 'center'
            }}>
            <Col style={{ flex: 1 }}>
              <h1 style={{ 
                fontFamily: 'Montserrat, sans-serif', 
                fontSize: 52,
                color: '#000'
              }}>Integrations</h1>
            </Col>
            <Col style={{ flex: 1 }}>
              <img src="/static/integrations.svg" alt="stack logo" style={{ height: '400px', width: '400px'  }} />
            </Col>
          </Layout>
        </CutPathDiv>
        <FooterSection />
    </App>
  )
}

export default Index;
